import base64
import struct

bmp_data = base64.b64decode(
    '')


def bmp_info(data):
    a = struct.unpack('<ccIIIIIIHH', data[0:30])
    if a[0:2] == (b'B', b'M') or (b'B', b'A'):
        print('true')

    return {

        'width': a[-4],

        'height': a[-3],

        'color': a[-1]

    }


bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')
